class GetEmail {
  email() {
    var admin = sessionStorage.getItem("admin");
    var doctor = sessionStorage.getItem("doctor");
    var patient = sessionStorage.getItem("patient");
    var email1 = "";
    var role = 0;
    if (admin) {
      var data2 = JSON.parse(admin);

      //var detials=JSON.parse( data2.data)
      var i = "";
      for (i in data2.data) {
        email1 = data2.data[i].email;

        console.log(email1);
        role = 1;
        return [email1, role];
      }
    } //end of if
    else if (doctor != null) {
      var data2 = JSON.parse(doctor);

      //var detials=JSON.parse( data2.data)
      var i = "";
      for (i in data2.data) {
        email1 = data2.data[i].email;

        console.log(email1);
        role = 2;
        return [email1, role];
      }
    } else if(patient != null){
      var data2 = JSON.parse(patient);

      //var detials=JSON.parse( data2.data)
      var i = "";
      for (i in data2.data) {
        email1 = data2.data[i].email;

        console.log(email1);
        role = 3;
        return [email1, role];
      }
    }
  }
}

export default new GetEmail();
