import React, { Component } from "react";
import axios from "axios";
import jsPDF from "jspdf";
import html2canvas from "html2canvas";
//import ImgToBase64 from "image-to-base64";

class Report extends Component {
  /*loadFile = (e) => {
    var image = document.getElementById("");
    // const imageToBase64 = ImgToBase64;
    image.src = URL.createObjectURL(e.target.files[0]);
    // imageToBase64(image.src);
  };*/
  printDocument() {
    const input = document.getElementById("output");
    html2canvas(input, { quality: 1, scale: 1 }).then((canvas) => {
      const imgData = canvas.toDataURL("image/png");
      const pdf = new jsPDF({ quality: 1, scale: 1, orientation: "landscape" });
      var width = pdf.internal.pageSize.getWidth();
      var height = pdf.internal.pageSize.getHeight();
      const pdfWidth = pdf.internal.pageSize.getWidth();
      const imgProps = pdf.getImageProperties(imgData);
      const pdfHeight = (imgProps.height * pdfWidth) / imgProps.width;
      pdf.addImage(imgData, "Jpeg", 0, 0, pdfWidth, pdfHeight);
      pdf.save("test.pdf");
    });

    /*
    const input = document.getElementById("output");
    html2canvas(input).then((canvas) => {
      var imgWidth = 200;
      var pageHeight = 290;
      var imgHeight = (canvas.height * imgWidth) / canvas.width;
      var heightLeft = imgHeight;
      const imgData = canvas.toDataURL("image/png");
      const pdf = new jsPDF("p", "mm", "a4");
      var position = 0;
      var heightLeft = imgHeight;
      pdf.addImage(imgData, "JPEG", 0, position, imgWidth, imgHeight);
      pdf.save("download.pdf");
    });
        /*
    var doc = new jsPDF("p", "pt");

    doc.text(20, 20, "This is the first title.");

    doc.setFont("helvetica");
    doc.setFontType("normal");
    doc.text(20, 60, "This is the second title.");

    doc.setFont("helvetica");
    doc.setFontType("normal");
    doc.text(20, 100, "This is the thrid title.");

    doc.save("demo.pdf");*/
  }
  work = () => {
    <div id="output" style={{ color: "black" }}>
      <h1 style={{ textAlign: "center", margin: "10%" }}>MID SYSTEM</h1>
      <p style={{ color: "black" }}>heloo </p>
      <table style={{ fontSize: "200px" }}>
        <tr>
          <td>1</td>
          <td>2</td>
          <td>3</td>
        </tr>
        <tr>
          <td>1</td>
          <td>2</td>
          <td>3</td>
        </tr>
        <tr>
          <td>1</td>
          <td>2</td>
          <td>3</td>
        </tr>
        <tr>
          <td>1</td>
          <td>2</td>
          <td>3</td>
        </tr>
        <tr>
          <td>1</td>
          <td>2</td>
          <td>3</td>
        </tr>
      </table>
    </div>;
  };
  render() {
    return (
      <div style={{ marginTop: "200px" }}>
        <div>
          <button>click to load </button>
          <button onClick={this.printDocument}>click to download </button>
        </div>
      </div>
    );
  }
}
export default Report;
