import React, { Component } from "react";
import { Link, Redirect } from "react-router-dom";

class Auth {
  constructor() {
    this.authenticated = false;
    this.auth = false;
    this.navigate = false;
    this.role = 0;
  }
  /*state = {
    auth: false,
    navigate: false,
    role: 0,
  };*/

  login(cb) {
    //fetch(`/user/check/?email=${email}&&pass=${pass}`)
    fetch(`/user/check/?email='bilal.khursheed617@gmail.com'&&pass=''`)
      .then((respone) => respone.json())
      // .then(() => setTimeout(() => this.setState({ navigate: true }), 2000))
      .then((Result) => {
        if (Result.status === "success") {
          if (Result.role === "doctor") {
            console.log("doctor Login successFully");
            //this.setState({ auth: true });
            //this.setState({ role: 1 });
            this.role = 1;
            // console.log("auth after login is ", this.state.auth);
            //setTimeout(() => this.setState({ navigate: true }), 20);
            setTimeout(() => (this.navigate = true), 20);
          } else if (Result.role === "Admin") {
            console.log("admin Login successFully");
            //this.setState({ auth: true });
            //this.setState({ role: 2 });
            this.role = 2;
            // console.log("auth after login is ", this.state.auth);
            setTimeout(() => (this.navigate = true), 20);
          } else if (Result.role === "patient") {
            console.log("patient Login successFully");
            //this.setState({ auth: true });
            //this.setState({ role: 3 });
            this.role = 3;
            // console.log("auth after login is ", this.state.auth);
            setTimeout(() => (this.navigate = true), 20);
          }
          //console.log("Login successFully");
          //this.setState({ auth: true });
          // console.log("auth after login is ", this.state.auth);
          //setTimeout(() => this.setState({ navigate: true }), 20);

          //<Redirect to="/admin"/>;
          this.authenticated = true;
          //return true;
        } else {
          alert("Sorry! Un-authenticated User");
          window.location.reload(false);
          return false;
        }
      })
      .catch((err) => console.error(err));
   // return false;

    cb();
  }

  logout(cb) {
    this.authenticated = false;
    cb();
  }

  isAuthenticated() {
    return this.authenticated;
  }
  isnavigation() {
    return this.state.navigate;
  }
  isRole() {
    return this.state.role;
  }
}

export default new Auth();
